// Icon mapping: technology name -> icon component
const getIconComponent = (techName: string) => {
  const iconMap: { [key: string]: () => JSX.Element } = {
    'Java': IconJava,
    'Python': IconPython,
    'React': IconReact,
    'Node.js': IconNodeJS,
    'TypeScript': IconTypeScript,
    'PHP': IconPHP,
    'Laravel': IconLaravel,
    'Next.js': IconNextJS,
    'MongoDB': IconMongoDB,
    'PostgreSQL': IconPostgreSQL,
    'MySQL': IconMySQL,
    'Prisma': IconPrisma,
    'WooCommerce': IconWooCommerce,
  };
  return iconMap[techName] || null;
};

